mapper.update:
UPDATE user_auth
SET auth = #{auth}
WHERE username = #{username}

mapper.select:
SELECT * FROM community WHERE creator_id = #{userId}
AND ( name LIKE CONCAT('%', #{keyword}, '%') OR description LIKE CONCAT('%', #{keyword}, '%') )
ORDER BY
CASE WHEN name LIKE CONCAT(#{keyword}, '%') THEN 0 WHEN name LIKE CONCAT('%', #{keyword}, '%') THEN 1 WHEN description LIKE CONCAT(#{keyword}, '%') THEN 2 WHEN description LIKE CONCAT('%', #{keyword}, '%') THEN 3 ELSE 4 END, created_at DESC
created_at DESC
LIMIT #{offset}, #{limit}

SELECT COUNT(*)
FROM comment c
WHERE c.user_id = #{userId}

SELECT c.id, bp.id AS post_id, bp.title, c.content, bp.community_id AS board_id, c.created_at, COUNT(ld.id) AS likes FROM comment c JOIN board_post bp ON bp.id = c.board_post_id LEFT JOIN likes_dislikes ld ON ld.target_id = c.id WHERE c.user_id = #{userId}
AND ( bp.title LIKE CONCAT('%', #{keyword}, '%') OR c.content LIKE CONCAT('%', #{keyword}, '%') )
GROUP BY c.id, bp.title, c.content, c.created_at ORDER BY
CASE WHEN bp.title LIKE CONCAT(#{keyword}, '%') THEN 0 WHEN bp.title LIKE CONCAT('%', #{keyword}, '%') THEN 1 WHEN c.content LIKE CONCAT(#{keyword}, '%') THEN 2 WHEN c.content LIKE CONCAT('%', #{keyword}, '%') THEN 3 ELSE 4 END, c.created_at DESC
c.created_at DESC
LIMIT #{offset}, #{limit}

SELECT bp.*, COALESCE(ld.like_count, 0) AS postLikes, COALESCE(c.comment_count, 0) AS commentCount FROM board_post bp LEFT JOIN ( SELECT target_id, COUNT(*) AS like_count FROM likes_dislikes WHERE type = 'post' AND is_likes = 1 GROUP BY target_id ) ld ON ld.target_id = bp.id LEFT JOIN ( SELECT board_post_id, COUNT(*) AS comment_count FROM comment GROUP BY board_post_id ) c ON c.board_post_id = bp.id WHERE bp.user_id = #{userId}
AND ( bp.title LIKE CONCAT('%', #{keyword}, '%') OR bp.content LIKE CONCAT('%', #{keyword}, '%') )
ORDER BY
CASE WHEN bp.title LIKE CONCAT(#{keyword}, '%') THEN 0 WHEN bp.title LIKE CONCAT('%', #{keyword}, '%') THEN 1 WHEN bp.content LIKE CONCAT(#{keyword}, '%') THEN 2 WHEN bp.content LIKE CONCAT('%', #{keyword}, '%') THEN 3 ELSE 4 END, bp.created_at DESC
bp.created_at DESC
LIMIT #{offset}, #{limit}

mapper.update:
UPDATE user
SET ban = #{ban}, ban_reason = #{reason}, ban_at = NOW()
WHERE id = #{id}

UPDATE user
SET ban = TRUE, ban_reason = #{reason}, ban_at = NOW()
WHERE id = #{userId}

UPDATE user_notification
SET is_enabled = CASE WHEN is_enabled = 1 THEN 0 ELSE 1 END
WHERE user_id = #{userId}
AND type = #{type}

mapper.select:
SELECT * FROM user
ORDER BY id DESC
LIMIT #{index}, #{size}

SELECT nickname
FROM user
WHERE id = #{userId}
